import { NavLink } from "react-router-dom";
import styled from "styled-components";

const Nav = styled.nav`
  display: flex;
  gap: 30px;
  padding: 15px 40px;
  background-color: #34495e;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2); 
  align-items: center;
  height: 60px;
`;

const StyledLink = styled(NavLink)`
  color: white;
  text-decoration: none;
  font-size: 18px; /* Increased font size */
  font-weight: 500; /* Slightly bolder text */
  padding: 10px 15px; /* Padding around the link for better touch area */
  border-radius: 5px; /* Slightly rounded link for a smoother look */
  transition: all 0.3s ease; /* Smooth transitions for hover effect */
  display: flex;
  align-items: center; /* Vertically center text */
  height: 100%; /* Make link fill the height of the navbar */

  &:hover {
    background-color: #1abc9c; /* Light turquoise on hover */
    color: #fff;
    transform: translateY(-2px); /* Slight elevation effect */
  }

  &.active {
    color: #e74c3c; /* Active link color */
    font-weight: 600;
    background-color: transparent;
    border-bottom: 3px solid #e74c3c;
  }
`;

const Navbar = () => {
  return (
    <Nav>
      <StyledLink to="/">Home</StyledLink>
      <StyledLink to="/books">All Books</StyledLink>
      <StyledLink to="/add">Add Book</StyledLink>
      <StyledLink to="/mybooks">My Books</StyledLink>
      <StyledLink to="/about">About</StyledLink>
    </Nav>
  );
};

export default Navbar;
