import React from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import PageWrapper from "../components/PageWrapper";
import HomeWrapper from "../components/HomeWrapper"; // Import the new HomeWrapper

// Section layout (full page width with spacing between sections)
const Section = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 80px 20px; /* Adjust padding for more space */
  margin-bottom: 30px; /* Space between sections */
  height: 100vh; /* Full viewport height */
  color: #333;
  position: relative;
  border-bottom: 1px solid #dcdcdc; /* Subtle grey border for separation */
  background-color: #fff; /* White background for the section */
  width: 100vw; /* Full width */

  /* Flexbox properties to allow the mosaics and content to properly adjust */
  @media (max-width: 768px) {
    flex-direction: column;
    padding: 40px 15px; /* Reduced padding on smaller screens */
  }
`;

// Create mosaic colors for each section
const Mosaic = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 50%;
  height: 100%;
  background: ${(props) => props.color};
  z-index: 1;
  opacity: 0.8; /* Slightly transparent for balance */

  /* Stack mosaics vertically on smaller screens */
  @media (max-width: 768px) {
    width: 100%;
    height: 50%; /* Each mosaic takes half of the section vertically */
    top: ${(props) => (props.left ? "0" : "50%")};
    left: 0;
  }
`;


const SectionTitle = styled.h2`
  font-size: 28px;
  font-weight: bold;
  color: #2c3e50;
  z-index: 2; /* Make sure title stays on top of the mosaic */
  text-align: center;
`;


const SectionDescription = styled.p`
  font-size: 16px;
  color: #7f8c8d;
  z-index: 2;
  text-align: center;
  margin-bottom: 20px;
`;

const StyledButton = styled(Link)`
  background-color: #3498db;
  color: white;
  padding: 15px 30px;
  border-radius: 8px;
  text-decoration: none;
  font-size: 18px;
  transition: background-color 0.3s;
  z-index: 2;

  &:hover {
    background-color: #2980b9;
  }
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 2;
`;

const Home = () => (
  <PageWrapper>
    <HomeWrapper>

      <Section>
        <Mosaic color="#C0FDFB" left />
        <Mosaic color="#FEE9E1" />
        <ContentWrapper>
          <SectionTitle>Welcome to My Book Library ðŸ“š</SectionTitle>
          <SectionDescription>
            Manage your book collection, discover new books, and keep your library up-to-date.
          </SectionDescription>
          <StyledButton to="/books">Explore All Books</StyledButton>
        </ContentWrapper>
      </Section>

      <Section>
        <Mosaic color="#FEE9E1" left />
        <ContentWrapper>
          <SectionTitle>Add a New Book</SectionTitle>
          <SectionDescription>
            Want to add a new book to your library? Use this section to add your personal collection.
          </SectionDescription>
          <StyledButton to="/add">Add Book</StyledButton>
        </ContentWrapper>
      </Section>
      <Section>
        <Mosaic color="#90E0EF" left />
        <Mosaic color="#F8F7FF" />
        <ContentWrapper>
          <SectionTitle>My Book Collection</SectionTitle>
          <SectionDescription>
            View and manage all the books you have added to your personal library.
          </SectionDescription>
          <StyledButton to="/mybooks">View My Books</StyledButton>
        </ContentWrapper>
      </Section>

      <Section>
        <Mosaic color="#F8F7FF" left />
        <Mosaic color="#C0FDFB" />
        <ContentWrapper>
          <SectionTitle>About This App</SectionTitle>
          <SectionDescription>
            Learn more about how this app works and how you can manage your book collection effortlessly.
          </SectionDescription>
          <StyledButton to="/about">Learn More</StyledButton>
        </ContentWrapper>
      </Section>
    </HomeWrapper>
  </PageWrapper>
);

export default Home;
