import axios from 'axios'

const apiKey = "AIzaSyB3DESYJ2L-dUafM9YJG8yzVa1pZ-3ETZE"


const fetchQuery = (type, crit, search) => {

  let url = `https://www.googleapis.com/books/v1/volumes?q=${search}&key=${apiKey}`
  if (crit === "Author")
    url = `https://www.googleapis.com/books/v1/volumes?q=inauthor:${search}&key=${apiKey}`
  if (crit === "ISBN")
    url = `https://www.googleapis.com/books/v1/volumes?q=isbn:${search}&key=${apiKey}`

  let books = []

  ;(async ()=>{
    try {
      const response = await axios.get(url)
      books = response.data.items
    } catch (error) {
      console.error("Error fetching data:", error)
    }
  })()

  return books

}

export default fetchQuery;
