import axios from 'axios'

const apiKey = "AIzaSyB3DESYJ2L-dUafM9YJG8yzVa1pZ-3ETZE"


const fetchQuery = (type, crit, search) => {

  let url = `https://www.googleapis.com/books/v1/volumes?q=${search}&key=${apiKey}&maxResults=40`
  if (crit === "Author")
    url = `https://www.googleapis.com/books/v1/volumes?q=inauthor:${search}&key=${apiKey}&maxResults=40`
  if (crit === "ISBN")
    url = `https://www.googleapis.com/books/v1/volumes?q=isbn:${search}&key=${apiKey}&maxResults=40`

  let books = []

  axios.get(url).then(res => {books = res.data.items; console.log(res.data)}).catch(err => console.log(err))
  

  console.log(url)
  console.log(books)

  return books

}

export default fetchQuery;
