import React, { useState, useEffect } from 'react';
import PageWrapper from "../components/PageWrapper";
import BookCard from '../components/BookCard';
import Mosaic from '../components/Mosaic';
import styled from 'styled-components';

// Grid for 3-column layout
const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-rows: 320px; // fixed height for Mosaic
  gap: 20px;
  margin-top: 20px;
`;

// Inner content wrapper: prevents card from stretching
const ContentWrapper = styled.div`
  <div
                style={{
                  border: '2px solid #ccc',
                  padding: '15px',
                  minHeight: '320px',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                  boxSizing: 'border-box'
                }}
              >
`;

// Remove button
const Button = styled.button`
  margin-top: 10px;
  padding: 6px 12px;
  border: none;
  border-radius: 4px;
  background-color: #ff4d4d;
  color: white;
  cursor: pointer;

  &:hover {
    background-color: #cc0000;
  }
`;

const MyBooks = () => {
    const [books, setBooks] = useState({});

    useEffect(() => {
        const storedBooks = JSON.parse(localStorage.getItem("books")) || {};
        setBooks(storedBooks);
    }, []);

    const removeLib = (isbn) => {
        const updatedBooks = { ...books };
        delete updatedBooks[isbn];
        setBooks(updatedBooks);
        localStorage.setItem("books", JSON.stringify(updatedBooks));
    };

    const bookList = Object.values(books);

    return (
        <PageWrapper>
            <h2>My Book Library</h2>

            {bookList.length === 0 ? (
                <p>No books in your library. Please add some!</p>
            ) : (
                <Grid>
                    {bookList.map((book) => (
                        <Mosaic key={book.isbn} {...book}>
                            <ContentWrapper>
                                <BookCard {...book} />
                                <Button onClick={() => removeLib(book.isbn)}>Remove</Button>
                            </ContentWrapper>
                        </Mosaic>
                    ))}
                </Grid>
            )}
        </PageWrapper>
    );
};

export default MyBooks;
