import React, { useState } from "react";
import styled from "styled-components";
import PageWrapper from "../components/PageWrapper";
import BookCard from "../components/BookCard";
import Mosaic from "../components/Mosaic";
import CardButton from "../components/CardButton";

// Styled components for layout and form controls
const Container = styled.div`
  margin: 20px;
  font-family: Arial, sans-serif;
`;

const Heading = styled.h2`
  font-size: 24px;
  font-weight: bold;
  color: #333;
  margin-bottom: 20px;
`;

const DropDownLi = styled.div`
  display: inline-block;
  position: relative;
  margin-right: 20px;
  font-size: 16px;
  color: #333;
  cursor: pointer;
`;

const DropDownContent = styled.div`
  position: absolute;
  top: 30px;
  left: 0;
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 100%;
  z-index: 1;
  display: none;
  padding: 5px;

  ${DropDownLi}:hover & {
    display: block;
  }

  div {
    padding: 8px 12px;
    cursor: pointer;

    &:hover {
      background-color: #f4f4f4;
    }
  }
`;

const Input = styled.input`
  padding: 8px 12px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 200px;
  margin-right: 10px;

  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const Button = styled.button`
  padding: 8px 16px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #0056b3;
  }
`;

const SearchWrapper = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const GridWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-rows: minmax(320px, auto);
  gap: 20px;
  margin-top: 20px;
`;

const AddBook = () => {
  const [criteria, setCriteria] = useState("Title");
  const [inputValue, setInputValue] = useState("");
  const [sort, setSort] = useState("relevance");
  const [books, setBooks] = useState([]);

  let myBooks = {};

  const handleSearch = async () => {
    setBooks([]);
    const result = await fetchQuery(null, criteria, inputValue, sort);
    setBooks(result || []);
  };

  const addtoLib = (book) => {
    alert("Book added to your library!");
    myBooks = JSON.parse(localStorage.getItem("books")) || {};
    myBooks[book.isbn] = book;
    localStorage.setItem("books", JSON.stringify(myBooks));
  };

  return (
    <PageWrapper>
      <Container>
        <Heading>Find Your Books</Heading>
        
        {/* Search Filters */}
        <SearchWrapper>
          <DropDownLi>
            Search By: {criteria}
            <DropDownContent>
              <div onClick={() => setCriteria("Title")}>Title</div>
              <div onClick={() => setCriteria("Author")}>Author</div>
              <div onClick={() => setCriteria("ISBN")}>ISBN</div>
            </DropDownContent>
          </DropDownLi>

          <DropDownLi>
            Sort By: {sort}
            <DropDownContent>
              <div onClick={() => setSort("relevance")}>relevance</div>
              <div onClick={() => setSort("newest")}>newest</div>
            </DropDownContent>
          </DropDownLi>

          <Input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder={`Enter ${criteria}`}
          />

          <Button onClick={handleSearch}>Search</Button>
        </SearchWrapper>

        {/* Book Grid */}
        <GridWrapper>
          {books.length > 0 &&
            books.map((book) => (
              <Mosaic key={book.isbn} {...book}>
                <div
                  style={{
                    border: "2px solid #ccc",
                    padding: "15px",
                    minHeight: "320px",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    boxSizing: "border-box",
                  }}
                >
                  <BookCard {...book} />
                  <CardButton variant="add" onClick={() => addtoLib(book)}>
                    Add to Library
                  </CardButton>
                </div>
              </Mosaic>
            ))}
        </GridWrapper>
      </Container>
    </PageWrapper>
  );
};

export default AddBook;
