import React, { useState } from "react";
import PageWrapper from "../components/PageWrapper";
import BackButton from "../components/BackButton";
import withLoading from "../HOC/withLoading";
import styled from "styled-components";

const Form = styled.form`
  display: flex;
  flex-direction: column;
  max-width: 400px;
  gap: 12px;
`;

const Input = styled.input`
  padding: 8px;
`;

const Button = styled.button`
  padding: 10px;
  background: #2c3e50;
  color: white;
  border: none;
  cursor: pointer;
`;

const AddBook = () => {

  return (
    <PageWrapper>
      <BackButton />
      <h3>Find Your Books:</h3>
      <div>
        Search By Title:<Input type="text" placeholder="Book Title" />
        Search By Author:<Input type="text" placeholder="Author Name" />
        Search By ISBN:<Input type="text" placeholder="ISBN Number" />
        <Button type="submit">Search</Button>
      </div>
      
    </PageWrapper>  
  );
};

export default withLoading(AddBook);
