import React, { useState, Component } from "react";
import PropTypes from "prop-types";
import styled, { ThemeProvider } from "styled-components";

import PageWrapper from "../components/PageWrapper";
import BackButton from "../components/BackButton";
import withLoading from "../HOC/withLoading";


const StyledUl = styled.ul`
    list-style-type: none;
    margin: 0;
    overflow: hidden;
    background-color: #333;
    padding: 0px;
    background-color: lightblue;
    color: black;
    font-weight: bold;
`;

const StyledLi = styled.li`
    float: left;
    height: 100%;
    padding: 10px;
    &:hover {
        background-color: red;
    }
`;

const DropDownContent = styled.div`
    display: none;
    position: absolute;
    min-width: 160px;
    z-index: 1;
`;

const DropDownLi = styled(StyledLi)`
    display: inline-block;
    &:hover ${DropDownContent} {
        display: block;
    }
`;

const StyledA = styled.a`
    display: inline-block;
    text-align: center;
    text-decoration: none;
`;

const SubA = styled(StyledA)`
    text-decoration: none;
    display: block;
    text-align: left;
    background-color: lightblue;
    padding: 10px;
`;
class Menu extends Component {
    handleClick = action => {
        if (!action) return;

        if (this.props.onClick) this.props.onClick(action);
    };

    render = () => {
        return (
            <StyledUl>
                <StyledLi>
                    <StyledA onClick={() => this.handleClick("Home")}>
                        Home
                    </StyledA>
                </StyledLi>
                <StyledLi>
                    <StyledA onClick={() => this.handleClick("News")}>
                        Home
                    </StyledA>
                </StyledLi>
                <DropDownLi>
                    <StyledA onClick={() => this.handleClick("DropDown")}>
                        DropDown
                    </StyledA>
                    <DropDownContent>
                        {" "}
                        <SubA onClick={() => this.handleClick("Link1")}>
                            Link 1
                        </SubA>
                        <SubA onClick={() => this.handleClick("Link2")}>
                            Link 2
                        </SubA>
                        <SubA onClick={() => this.handleClick("Link3")}>
                            Link 3
                        </SubA>
                    </DropDownContent>
                </DropDownLi>
            </StyledUl>
        );
    };
}

const AddBook = () => {

  return (
    <PageWrapper>
      <BackButton />
      <h3>Find Your Books:</h3>
      
      
    </PageWrapper>  
  );
};

export default withLoading(Menu);
