import React, { useState} from "react";
import styled from "styled-components";

import PageWrapper from "../components/PageWrapper";
import BackButton from "../components/BackButton";
import withLoading from "../HOC/withLoading";
import fetchQuery from "../hooks/fetchQuery";



const DropDownContent = styled.div`
    display: none;
    position: absolute;
    min-width: 160px;
    z-index: 1;
`;

const DropDownLi = styled.div`
    display: inline-block;
    &:hover ${DropDownContent} {
        display: block;
    }
`;

const Input = styled.input`
    margin-top: 20px;
    margin-left: 20px;
    padding: 2px;
    width: 300px;
    font-size: 16px;
`;



const AddBook = () => {
  const [criteria, setCriteria] = useState("Title");
  const [inputValue, setInputValue] = useState("");
  const [books , setBooks] = useState([]);


  return (
    <PageWrapper>
      <div>
      <h2>Find Your Books</h2>
      <DropDownLi>
        Search By: {criteria}
        <DropDownContent>
          <div onClick={() => setCriteria("Title")}>Title</div>
          <div onClick={() => setCriteria("Author")}>Author</div>
          <div onClick={() => setCriteria("ISBN")}>ISBN</div>
        </DropDownContent>
      </DropDownLi>
      
      <Input type="text" value={inputValue} onChange={(e) => setInputValue(e.target.value)} placeholder={`Enter ${criteria}`} />
      <button style={{marginLeft: '10px', padding: '5px 10px'}} onClick={() => setBooks(fetchQuery(null, criteria, inputValue) || [])}>Search</button>
      </div>
      <div>
        {books && books.map(book => (
          <div key={book.id} style={{border: '1px solid black', margin: '10px', padding: '10px', width: '300px'}}>
            <h3>{book.volumeInfo.title}</h3>
            <p>Author: {book.volumeInfo.authors ? book.volumeInfo.authors.join(', ') : 'N/A'}</p>
            <p>Published Date: {book.volumeInfo.publishedDate || 'N/A'}</p>
            {book.volumeInfo.imageLinks && <img src={book.volumeInfo.imageLinks.thumbnail} alt={book.volumeInfo.title} />}
          </div>
        ))}
      </div>
    </PageWrapper>  
  );
};

export default withLoading(AddBook);
