import React, { useState, useEffect } from 'react';
import PageWrapper from "../components/PageWrapper";
import BookCard from '../components/BookCard';
import Mosaic from '../components/Mosaic'; // Make sure Mosaic is imported
import styled from 'styled-components';

// Grid wrapper for consistent 3-column layout
const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-rows: 320px; /* fixed height for all cards */
  gap: 20px;
  margin-top: 20px;
`;

// Styled Remove button
const Button = styled.button`
  margin-top: auto;
  padding: 6px 12px;
  border: none;
  border-radius: 4px;
  background-color: #ff4d4d;
  color: white;
  cursor: pointer;

  &:hover {
    background-color: #cc0000;
  }
`;

const MyBooks = () => {
    const [books, setBooks] = useState({});

    // Load books from localStorage
    useEffect(() => {
        const storedBooks = JSON.parse(localStorage.getItem("books")) || {};
        setBooks(storedBooks);
    }, []);

    // Remove a book
    const removeLib = (isbn) => {
        const updatedBooks = { ...books };
        delete updatedBooks[isbn];
        setBooks(updatedBooks);
        localStorage.setItem("books", JSON.stringify(updatedBooks));
    };

    const bookList = Object.values(books);

    return (
        <PageWrapper>
            <h2>My Book Library</h2>

            {bookList.length === 0 ? (
                <p>No books in your library. Please add some!</p>
            ) : (
                <Grid>
                    {bookList.map((book) => (
                        <Mosaic key={book.isbn} {...book}>
                            <BookCard {...book} />
                            <Button onClick={() => removeLib(book.isbn)}>Remove</Button>
                        </Mosaic>
                    ))}
                </Grid>
            )}
        </PageWrapper>
    );
};

export default MyBooks;
