import React, { useState } from "react";
import styled from "styled-components";
import PageWrapper from "../components/PageWrapper";
import BookCard from "../components/BookCard";
import Mosaic from "../components/Mosaic";
import CardButton from "../components/CardButton";

// Styled components
const Container = styled.div`
  margin: 20px;
  font-family: Arial, sans-serif;
`;

const Heading = styled.h2`
  font-size: 24px;
  font-weight: bold;
  color: #333;
  margin-bottom: 20px;
`;

const DropDownWrapper = styled.div`
  position: relative;
  display: inline-block;
`;

const DropDownButton = styled.button`
  padding: 10px;
  background-color: #34495e;
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 16px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 5px;
`;

const DropDownContent = styled.div`
  position: absolute;
  top: 35px;
  left: 0;
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 180px;
  z-index: 1;
  display: ${(props) => (props.open ? "block" : "none")};
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
`;

const DropDownItem = styled.div`
  padding: 8px;
  cursor: pointer;

  &:hover {
    background-color: #f4f4f4;
  }
`;

const Input = styled.input`
  padding: 8px 12px;
  font-size: 16px;
  border: 1px solid #ccc;
  border-radius: 4px;
  width: 200px;
  margin-right: 10px;

  &:focus {
    outline: none;
    border-color: #007bff;
  }
`;

const Button = styled.button`
  padding: 8px 16px;
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #0056b3;
  }
`;

const SearchWrapper = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const GridWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-auto-rows: minmax(320px, auto);
  gap: 20px;
  margin-top: 20px;
`;

const AddBook = () => {
  const [criteria, setCriteria] = useState("Title");
  const [inputValue, setInputValue] = useState("");
  const [sort, setSort] = useState("relevance");
  const [books, setBooks] = useState([]);
  const [searchDropDownOpen, setSearchDropDownOpen] = useState(false);
  const [sortDropDownOpen, setSortDropDownOpen] = useState(false);

  let myBooks = {};

  const handleSearch = async () => {
    setBooks([]);
    const result = await fetchQuery(null, criteria, inputValue, sort);
    setBooks(result || []);
  };

  const addtoLib = (book) => {
    alert("Book added to your library!");
    myBooks = JSON.parse(localStorage.getItem("books")) || {};
    myBooks[book.isbn] = book;
    localStorage.setItem("books", JSON.stringify(myBooks));
  };

  // Toggle the visibility of the "Search By" dropdown
  const toggleSearchDropDown = () => {
    setSearchDropDownOpen(!searchDropDownOpen);
    setSortDropDownOpen(false); // Close the "Sort By" dropdown when toggling "Search By"
  };

  // Toggle the visibility of the "Sort By" dropdown
  const toggleSortDropDown = () => {
    setSortDropDownOpen(!sortDropDownOpen);
    setSearchDropDownOpen(false); // Close the "Search By" dropdown when toggling "Sort By"
  };

  // Handle dropdown item click
  const handleDropDownItemClick = (newCriteria) => {
    setCriteria(newCriteria);
    setSearchDropDownOpen(false); // Close the dropdown when an item is clicked
  };

  return (
    <PageWrapper>
      <Container>
        <Heading>Find Your Books</Heading>

        {/* Search Filters */}
        <SearchWrapper>
          {/* Search By Dropdown */}
          <DropDownWrapper>
            <DropDownButton onClick={toggleSearchDropDown}>
              Search By: {criteria}
            </DropDownButton>
            <DropDownContent open={searchDropDownOpen}>
              <DropDownItem onClick={() => handleDropDownItemClick("Title")}>
                Title
              </DropDownItem>
              <DropDownItem onClick={() => handleDropDownItemClick("Author")}>
                Author
              </DropDownItem>
              <DropDownItem onClick={() => handleDropDownItemClick("ISBN")}>
                ISBN
              </DropDownItem>
            </DropDownContent>
          </DropDownWrapper>

          {/* Sort By Dropdown */}
          <DropDownWrapper>
            <DropDownButton onClick={toggleSortDropDown}>
              Sort By: {sort}
            </DropDownButton>
            <DropDownContent open={sortDropDownOpen}>
              <DropDownItem onClick={() => setSort("relevance")}>
                Relevance
              </DropDownItem>
              <DropDownItem onClick={() => setSort("newest")}>
                Newest
              </DropDownItem>
            </DropDownContent>
          </DropDownWrapper>

          <Input
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            placeholder={`Enter ${criteria}`}
          />

          <Button onClick={handleSearch}>Search</Button>
        </SearchWrapper>

        {/* Book Grid */}
        <GridWrapper>
          {books.length > 0 &&
            books.map((book) => (
              <Mosaic key={book.isbn} {...book}>
                <div
                  style={{
                    border: "2px solid #ccc",
                    padding: "15px",
                    minHeight: "320px",
                    display: "flex",
                    flexDirection: "column",
                    alignItems: "center",
                    boxSizing: "border-box",
                  }}
                >
                  <BookCard {...book} />
                  <CardButton variant="add" onClick={() => addtoLib(book)}>
                    Add to Library
                  </CardButton>
                </div>
              </Mosaic>
            ))}
        </GridWrapper>
      </Container>
    </PageWrapper>
  );
};

export default AddBook;
